import React, {useEffect, useState} from "react";
import axios from "axios";
import './electronics.css';
import Modal from "react-modal";

function Electronics() {
    const API_URL = 'http://localhost:8080/api/apartments';
    const [advertisements, setAdvertisements] = useState([]);

    const [newApartment, setNewApartment] = useState({
        type: '',
        rentOrSell: '',
        size: '',
        rooms: '',
        price: '',
    });
    const [showPopup, setShowPopup] = useState(false);
    const [editApartmentId, setEditApartmentId] = useState(null);
    const [minPriceFilter, setMinPriceFilter] = useState('');
    const [maxPriceFilter, setMaxPriceFilter] = useState('');

    const handleMinPriceFilter = (e) => {
        setMinPriceFilter(e.target.value);
    };

    const handleMaxPriceFilter = (e) => {
        setMaxPriceFilter(e.target.value);
    };
    useEffect(() => {
        fetchApartments().then(r => console.log(r));
    }, []);

    const handleCancel = () => {
        setShowPopup(false);
        setNewApartment({
            type: '',
            rentOrSell: '',
            size: '',
            rooms: '',
            price: '',
        });
        setEditApartmentId(null);
    };

        const fetchApartments = async () => {
            try {
                const response = await axios.get(API_URL, {
                    headers: {
                        "Accept": "application/json",
                        "Access-Control-Allow-Origin": "*",
                        "Content-Type": "application/json",
                        "Access-Control-Allow-Methods": "GET,POST,OPTIONS,DELETE,PUT"
                    }});
                setAdvertisements(response.data);
            } catch (error) {
                console.error('Error fetching apartments:', error);
            }
        };

        const handleSort = () => {
            // Implement sorting logic here and update the 'apartments' state.
        };
        const filteredAdvertisements = advertisements.filter((apartment) => {
            if (!minPriceFilter && !maxPriceFilter) return true;

            const price = parseInt(apartment.price);

            if (minPriceFilter && !maxPriceFilter) {
                return price >= parseInt(minPriceFilter);
            }
            if (!minPriceFilter && maxPriceFilter) {
                return price <= parseInt(maxPriceFilter);
            }

            return price >= parseInt(minPriceFilter) && price <= parseInt(maxPriceFilter);
        });

        const handleAdd = () => {
            setShowPopup(true);
            setNewApartment({
                type: '',
                rentOrSell: '',
                size: '',
                rooms: '',
                price: '',
            });
            setEditApartmentId(null);
        };

        const handleEdit = (apartment) => {
            setShowPopup(true);
            setNewApartment({ ...apartment });
            setEditApartmentId(apartment.id);
        };

        const handleDelete = async (apartmentId) => {
            try {
                await axios.delete(`${API_URL}/${apartmentId}`,{
                    headers: {
                        "Accept": "application/json",
                        "Access-Control-Allow-Origin": "*",
                        "Content-Type": "application/json",
                        "Access-Control-Allow-Methods": "GET,POST,OPTIONS,DELETE,PUT"
                    }});
                await fetchApartments();
            } catch (error) {
                console.error('Error deleting apartment:', error);
            }
        };

        const handleFormChange = (e) => {
            const { name, value } = e.target;
            setNewApartment((prevApartment) => ({
                ...prevApartment,
                [name]: value,
            }));
        };

        const handleSubmit = async (e) => {
            e.preventDefault();
            try {
                if (editApartmentId) {
                    await axios.put(`${API_URL}/${editApartmentId}`, newApartment, {
                        headers: {
                            "Accept": "application/json",
                            "Access-Control-Allow-Origin": "*",
                            "Content-Type": "application/json",
                            "Access-Control-Allow-Methods": "GET,POST,OPTIONS,DELETE,PUT"
                        }});
                } else {
                    await axios.post(API_URL, newApartment, {
                        headers: {
                            "Accept": "application/json",
                            "Access-Control-Allow-Origin": "*",
                            "Content-Type": "application/json",
                            "Access-Control-Allow-Methods": "GET,POST,OPTIONS,DELETE,PUT"
                        }});
                }
                setShowPopup(false);
                await fetchApartments();
            } catch (error) {
                console.error('Error submitting apartment:', error);
            }
        };

        return (
            <div>
                <h1>Apartment Ads</h1>
                <button onClick={handleSort}>Sort</button>
                <button onClick={() => handleSort('type')}>Sort by Type</button>
                <button onClick={() => handleSort('price')}>Sort by Price</button>
                <button onClick={handleAdd}>Add New Ad</button>

                <label>
                    <input type="number" value={minPriceFilter} min={0} max={maxPriceFilter} onChange={handleMinPriceFilter} />
                </label>
                <label>
                    <input type="number" value={maxPriceFilter} min={minPriceFilter} onChange={handleMaxPriceFilter} />
                </label>

                <ul>
                    {filteredAdvertisements.map((apartment) => (
                        <li key={apartment.id}>
                            <div>
                                <div>
                                    <div>Type: {apartment.type}</div>
                                    <div>Rent or Sell: {apartment.rentOrSell}</div>
                                    <div>Size: {apartment.size}</div>
                                    <div>Rooms: {apartment.rooms}</div>
                                    <div>Price: {apartment.price}</div>
                                </div>
                                <div>
                                    <button onClick={() => handleEdit(apartment)}>Edit</button>
                                    <button onClick={() => handleDelete(apartment.id)}>Delete</button>
                                </div>
                            </div>
                        </li>
                    ))}
                </ul>
                {/*<table>*/}
                {/*    <thead>*/}
                {/*    <tr>*/}
                {/*        <th>Type</th>*/}
                {/*        <th>Rent or Sell</th>*/}
                {/*        <th>Size</th>*/}
                {/*        <th>Rooms</th>*/}
                {/*        <th>Price</th>*/}
                {/*        <th>Actions</th>*/}
                {/*    </tr>*/}
                {/*    </thead>*/}
                {/*    <tbody>*/}
                {/*    {advertisements.map((apartment) => (*/}
                {/*        <tr key={apartment.id}>*/}
                {/*            <td>{apartment.type}</td>*/}
                {/*            <td>{apartment.rentOrSell}</td>*/}
                {/*            <td>{apartment.size}</td>*/}
                {/*            <td>{apartment.rooms}</td>*/}
                {/*            <td>{apartment.price}</td>*/}
                {/*            <td>*/}
                {/*                <button onClick={() => handleEdit(apartment)}>Edit</button>*/}
                {/*                <button onClick={() => handleDelete(apartment.id)}>Delete</button>*/}
                {/*            </td>*/}
                {/*        </tr>*/}
                {/*    ))}*/}
                {/*    </tbody>*/}
                {/*</table>*/}

                <Modal isOpen={showPopup} ariaHideApp={false} title={editApartmentId ? 'Edit Ad' : 'Add New Ad'} onClose={() => setShowPopup(false)}>
                    <form onSubmit={handleSubmit}>
                    <div>
                    <h2>{editApartmentId ? 'Edit Ad' : 'Add New Ad'}</h2>
                        <label>Type: </label>
                        <input
                            type="text"
                            name="type"
                            value={newApartment.type}

                            onChange={handleFormChange}
                        />
                        <br />
                        <label>Rent or Sell: </label>
                        <input
                            type="text"
                            name="rentOrSell"
                            value={newApartment.rentOrSell}
                            onChange={handleFormChange}
                        />
                        <br />
                        <label>Size: </label>
                        <input
                            type="text"
                            name="size"
                            value={newApartment.size}
                            onChange={handleFormChange}
                        />
                        <br />
                        <label>Rooms: </label>
                        <input
                            type="text"
                            name="rooms"
                            value={newApartment.rooms}
                            onChange={handleFormChange}
                        />
                        <br />
                        <label>Price: </label>
                        <input
                            type="text"
                            name="price"
                            value={newApartment.price}
                            onChange={handleFormChange}
                        />
                        <br />
                        <button type="submit">Save Ad</button>
                        <button type="button" onClick={handleCancel}>Cancel</button>
                    </div>
                    </form>
                </Modal>
            </div>
        );
    };
export default Electronics;
