import React, {useEffect, useState} from "react";
import axios from "axios";
import './apartments.css';
import EditAdvertisement from "./editAdvertisement";
import {useParams} from "react-router-dom";
import Modal from "react-modal";

function Apartments() {
    const [advertisements, setAdvertisements] = useState([]);
    const { id } = useParams();
    const [isModalOpen, setIsModalOpen] = useState(false);

    const [formData, setFormData] = useState({
        type: '',
        rentOrSell: '',
        size: 10,
        rooms: 1,
        price: 1,
    });
    const handleOpenModal = () => {
        setIsModalOpen(true);
    };

    const handleCloseModal = () => {
        setIsModalOpen(false);
    };

    useEffect(() => {
        // Fetch the list of advertisements from the backend API
        axios.get('http://localhost:8080/api/apartments', {
            headers: {
                "Accept": "application/json",
                "access-Control-Allow-Origin": "*",
                "content-Type": "application/json"
            }
        })
            .then((response) => {
                console.log(response.data)
                setAdvertisements([...response.data]);
            })
            .catch((error) => {
                console.error('Error fetching advertisements:', error);
            });
    }, [id, formData]);

    const handleChange = (e) => {
        console.log(formData)
        const value = e.target.value;
        setFormData({
            ...formData,
            [e.target.name]: value,
        });
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        const data = {
            type: formData.type,
            rentOrSell: formData.rentOrSell,
            size: formData.size,
            rooms: formData.rooms,
            price: formData.price,

        };
        alert(`The name you entered was: ${formData.type}`);
        console.log(data)
        axios.post('http://localhost:8080/api/apartments', data, {
            headers: {
                "Accept": "application/json",
                "Access-Control-Allow-Origin": "*",
                "Content-Type": "application/json",
                "Access-Control-Allow-Methods": "GET,POST,OPTIONS,DELETE,PUT"
            }}) // Change the API endpoint accordingly for other categories
            .then((response) => {
                console.log('Advertisement added:', response.data);

                // Perform any additional actions after successful submission
                // For example, redirect to the advertisement list
                setAdvertisements([...response.data]);
                setIsModalOpen(false);
            })
            .catch((error) => {
                console.error('Error adding advertisement:', error);
            });
    }

    return (
        <div>
            <h2>Add Advertisement</h2>

            <Modal
                isOpen={isModalOpen}
                onRequestClose={handleCloseModal}
                contentLabel="Add Advertisement Modal"
            >
                <h2>Edit Advertisement</h2>
                <form onSubmit={handleSubmit}>
                    <label>Enter apartment type:
                        <input
                            type="text"
                            name="type"
                            value={formData.type}
                            onChange={handleChange}
                        />
                    </label>
                    <label>Choose for rent or sell:
                        <label htmlFor="rent">rent</label>
                        <input
                            type="radio"
                            name="rentOrSell"
                            value="rent"
                            checked={formData.rentOrSell === 'rent'}
                            onChange={handleChange}
                        />
                        <label htmlFor="sell">sell</label>
                        <input
                            type="radio"
                            name="rentOrSell"
                            value="sell"
                            checked={formData.rentOrSell === 'sell'}
                            onChange={handleChange}
                        />

                    </label>
                    <label>Enter your rooms:
                        <input
                            type="number"
                            name="rooms"
                            value={formData.rooms}
                            onChange={handleChange}
                        />
                    </label>
                    <label>Enter your size:
                        <input
                            type="number"
                            name="size"
                            value={formData.size}
                            onChange={handleChange}
                        />
                    </label>
                    <label>Enter your price:
                        <input
                            type="number"
                            name="price"
                            value={formData.price}
                            onChange={handleChange}
                        />
                    </label>
                    {/* Add other fields */}
                    <button type="submit">Add Advertisement</button>
                    {/*<button type="button" >Delete Advertisement</button>*/}
                    <button type="button" onClick={handleCloseModal}>Cancel</button>
                </form>
            </Modal>

            <div className="title"><h2>Apartments List</h2><button onClick={handleOpenModal}>Add ad</button></div>
            {advertisements.length === 0 ? (
                <p>No advertisements found.</p>
            ) : (
                <table>
                    <tr>
                        <th>Type</th>
                        <th>Rent\Sell</th>
                        <th>Size</th>
                        <th>Rooms</th>
                        <th>Price</th>
                    </tr>
                    {advertisements.map((ad) => (
                        <tr key={ad.id}>
                            <td>{ad.type}</td>
                            <td>{ad.rentOrSell}</td>
                            <td>{ad.size}</td>
                            <td>{ad.rooms}</td>
                            <td>{ad.price}</td>
                            <button onClick={EditAdvertisement}>Edit</button>
                            <button>Delete</button>
                        </tr>
                    ))}
                </table>
            )}
        </div>
    );
}
 export default Apartments;
