import {useEffect, useState} from "react";
import axios from "axios";

function App() {
    const [advertisements, setAdvertisements] = useState([]);

    useEffect(() => {
        // Fetch the list of advertisements from the backend API
        axios.get('http://localhost:8080/api/apartments', {
            headers: {"Accept": "application/json", "access-Control-Allow-Origin": "*", "content-Type": "application/json"}})
            .then((response) => {
                console.log(response.data)
                setAdvertisements(response.data);
            })
            .catch((error) => {
                console.error('Error fetching advertisements:', error);
            });
    }, []);

    return (
        <div>
            <h2>Advertisement List</h2>
            {advertisements.length === 0 ? (
                <p>No advertisements found.</p>
            ) : (
                <table>
                    <tr>
                        <th>Type</th>
                        <th>Rent\Sell</th>
                        <th>Size</th>
                        <th>Rooms</th>
                        <th>Price</th>
                    </tr>
                    {advertisements.map((ad) => (
                        <tr key={ad.id}>
                            {/* Display the advertisement details here */}
                             <td>{ad.type}</td>
                                    <td>{ad.rentOrSell}</td>
                                    <td>{ad.size}</td>
                                    <td>{ad.rooms}</td>
                                    <td>{ad.price}</td>
                        </tr>
                    ))}
                </table>
            )}
        </div>
    );
}

export default App;
