import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useHistory } from 'react-router-dom';

const Advertisement = () => {
    const { id } = useParams();
    const history = useHistory();

    const [formData, setFormData] = useState({
        type: '',
        // Add other fields here (e.g., rentOrSell, size, rooms, price, etc.)
    });

    useEffect(() => {
        // Fetch the advertisement data from the backend API based on the provided ID
        axios.get(`/api/apartments/${id}`) // Change the API endpoint accordingly for other categories
            .then((response) => {
                setFormData(response.data);
            })
            .catch((error) => {
                console.error('Error fetching advertisement:', error);
            });
    }, [id]);

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        });
    };

    const handleDelete = () => {
        // Send a DELETE request to the backend API to delete the advertisement
        axios.delete(`/api/apartments/${id}`) // Change the API endpoint accordingly for other categories
            .then((response) => {
                console.log('Advertisement deleted:', response.data);
                // Navigate back to the advertisement list after successful deletion
                history.push('/advertisements');
            })
            .catch((error) => {
                console.error('Error deleting advertisement:', error);
            });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Send the updated advertisement data to the backend API for saving changes
        axios.put(`/api/apartments/${id}`, formData) // Change the API endpoint accordingly for other categories
            .then((response) => {
                console.log('Advertisement updated:', response.data);
                // Perform any additional actions after successful update
            })
            .catch((error) => {
                console.error('Error updating advertisement:', error);
            });
    };

    const handleAdd = () => {
        // Send the new advertisement data to the backend API for adding
        axios.post('/api/apartments', formData) // Change the API endpoint accordingly for other categories
            .then((response) => {
                console.log('Advertisement added:', response.data);
                // Perform any additional actions after successful submission
                // For example, redirect to the advertisement list
                history.push('/advertisements');
            })
            .catch((error) => {
                console.error('Error adding advertisement:', error);
            });
    };

    return (
        <div>
            <h2>Edit Advertisement</h2>
            <form onSubmit={handleSubmit}>
                {/* Display existing advertisement data in the form */}
                <label>
                    Type:
                    <input type="text" name="type" value={formData.type} onChange={handleChange} />
                </label>
                {/* Add other fields */}
                <button type="submit">Update Advertisement</button>
                <button type="button" onClick={handleDelete}>Delete Advertisement</button>
            </form>

            {/* Add a separate form for adding a new advertisement */}
            <h2>Add Advertisement</h2>
            <form onSubmit={handleAdd}>
                <label>
                    Type:
                    <input type="text" name="type" value={formData.type} onChange={handleChange} />
                </label>
                {/* Add other fields */}
                <button type="submit">Add Advertisement</button>
            </form>
        </div>
    );
};

export default Advertisement;
