import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const Advertisement = () => {
    const { id } = useParams();
    const navigate = useNavigate();

    const [formData, setFormData] = useState({
        type: '',
        size: 0
        // Add other fields here (e.g., rentOrSell, size, rooms, price, etc.)
    });
    const [type, setType] = useState("");


    // useEffect(() => {
    //     // Fetch the advertisement data from the backend API based on the provided ID
    //     axios.get(`http://localhost:8080/api/apartments`, {
    //         headers: {
    //             "Accept": "application/json",
    //             "access-Control-Allow-Origin": "*",
    //             "content-Type": "application/json"
    //         }}) // Change the API endpoint accordingly for other categories
    //         .then((response) => {
    //             setFormData(response.data);
    //         })
    //         .catch((error) => {
    //             console.error('Error fetching advertisement:', error);
    //         });
    // }, [id]);

    const handleChange = (e) => {
        console.log(formData)
        const value = e.target.value;
        setFormData({
            ...formData,
            [e.target.name]: value,
        });
    };

    // const handleDelete = () => {
    //     // Send a DELETE request to the backend API to delete the advertisement
    //     axios.delete(`http://localhost:8080/api/apartments/3`, {
    //         headers: {
    //             "Accept": "application/json",
    //             "access-Control-Allow-Origin": "*",
    //             "content-Type": "application/json",
    //             "Access-Control-Allow-Methods": "GET,POST,OPTIONS,DELETE,PUT"
    //         }}) // Change the API endpoint accordingly for other categories
    //         .then((response) => {
    //             console.log('Advertisement deleted:', response.data);
    //             // Navigate back to the advertisement list after successful deletion
    //             navigate(-1);
    //         })
    //         .catch((error) => {
    //             console.error('Error deleting advertisement:', error);
    //         });
    // };

    // const handleSubmit = (e) => {
    //     e.preventDefault();
    //     // Send the updated advertisement data to the backend API for saving changes
    //     axios.put(`http://localhost:8080/api/apartments/1`, formData, {
    //         headers: {
    //             "Accept": "application/json",
    //             "access-Control-Allow-Origin": "*",
    //             "content-Type": "application/json",
    //             "Access-Control-Allow-Methods": "GET,POST,OPTIONS,DELETE,PUT"
    //         }}) // Change the API endpoint accordingly for other categories
    //         .then((response) => {
    //             console.log('Advertisement updated:', response.data);
    //             // Perform any additional actions after successful update
    //         })
    //         .catch((error) => {
    //             console.error('Error updating advertisement:', error);
    //         });
    // };
    const handleSubmit = (event) => {
        event.preventDefault();
        const data = {
            type: formData.type,
            rentOrSell: formData.rentOrSell,
            size: formData.type,

        };
        alert(`The name you entered was: ${formData.type}`);
        console.log(data)
        axios.post('http://localhost:8080/api/apartments', data, {
            headers: {
                "Accept": "application/json",
                "Access-Control-Allow-Origin": "*",
                "Content-Type": "application/json",
                "Access-Control-Allow-Methods": "GET,POST,OPTIONS,DELETE,PUT"
            }}) // Change the API endpoint accordingly for other categories
            .then((response) => {
                console.log('Advertisement added:', response.data);
                // Perform any additional actions after successful submission
                // For example, redirect to the advertisement list
                // navigate(-1);
            })
            .catch((error) => {
                console.error('Error adding advertisement:', error);
            });
    }

    return (
        <div>
            {/*<h2>Edit Advertisement</h2>*/}
            {/*<form onSubmit={handleSubmit}>*/}
            {/*    /!* Display existing advertisement data in the form *!/*/}
            {/*    <label>*/}
            {/*        Type:*/}
            {/*        <input type="text" name="type" value={formData.type} onChange={handleChange} />*/}
            {/*    </label>*/}
            {/*    /!* Add other fields *!/*/}
            {/*    <button type="submit">Update Advertisement</button>*/}
            {/*    <button type="button" onClick={handleDelete}>Delete Advertisement</button>*/}
            {/*</form>*/}

            {/* Add a separate form for adding a new advertisement */}
            <h2>Add Advertisement</h2>
            <form onSubmit={handleSubmit}>
                <label>Enter apartment type:
                    <input
                        type="text"
                        name="type"
                        value={formData.type}
                        onChange={handleChange}
                    />
                </label>
                <label>Choose for rent or sell:
                    <label for="rent">rent</label>
                    <input
                        type="radio"
                        name="rentOrSell"
                        value={formData.rentOrSell}
                        onChange={handleChange}
                    />
                    <label for="sell">sell</label>
                    <input
                        type="radio"
                        name="rentOrSell"
                        value={formData.rentOrSell}
                        onChange={handleChange}
                    />

                </label>
                <label>Enter your size:
                    <input
                        type="number"
                        name="size"
                        value={formData.size}
                        onChange={handleChange}
                    />
                </label>
                <button type="submit">Send</button>
            </form>
        </div>
    );
};

export default Advertisement;
