import {useEffect, useState} from "react";
import axios from "axios";
import './apartments.css';
import EditAdvertisement from "./editAdvertisement";

function Apartments() {
    const [advertisements, setAdvertisements] = useState([]);
    const [id, setId] = useState(0);
    const [formData, setFormData] = useState({
        type: '',
        rentOrSell: '',
        size: 10,
        rooms: 1,
        price: 1,
        // Add other fields here (e.g., rentOrSell, size, rooms, price, etc.)
    });

    useEffect(() => {
        // Fetch the list of advertisements from the backend API
        axios.get('http://localhost:8080/api/apartments', {
            headers: {
                "Accept": "application/json",
                "access-Control-Allow-Origin": "*",
                "content-Type": "application/json"
            }
        })
            .then((response) => {
                console.log(response.data)
                setAdvertisements([...response.data]);
            })
            .catch((error) => {
                console.error('Error fetching advertisements:', error);
            });
    }, [id, formData]);

    return (
        <div>
            <div className="title"><h2>Apartments List</h2><button>Add ad</button></div>
            {advertisements.length === 0 ? (
                <p>No advertisements found.</p>
            ) : (
                <table>
                    <tr>
                        <th>Type</th>
                        <th>Rent\Sell</th>
                        <th>Size</th>
                        <th>Rooms</th>
                        <th>Price</th>
                    </tr>
                    {advertisements.map((ad) => (
                        <tr key={ad.id}>
                            <td>{ad.type}</td>
                            <td>{ad.rentOrSell}</td>
                            <td>{ad.size}</td>
                            <td>{ad.rooms}</td>
                            <td>{ad.price}</td>
                            <button onClick={EditAdvertisement}>Edit</button>
                            <button>Delete</button>
                        </tr>
                    ))}
                </table>
            )}
        </div>
    );
}
 export default Apartments;
