import {useEffect, useState} from "react";
import axios from "axios";
import './apartments.css';

function Apartments() {
    const [advertisements, setAdvertisements] = useState([]);

    useEffect(() => {
        // Fetch the list of advertisements from the backend API
        axios.get('http://localhost:8080/api/apartments', {
            headers: {
                "Accept": "application/json",
                "access-Control-Allow-Origin": "*",
                "content-Type": "application/json"
            }
        })
            .then((response) => {
                console.log(response.data)
                setAdvertisements(response.data);
            })
            .catch((error) => {
                console.error('Error fetching advertisements:', error);
            });
    }, []);

    const EditApartmentsAd = () => {
        const {id} = useParams();
        const [formData, setFormData] = useState({
            type: '',
            // Add other fields here (e.g., rentOrSell, size, rooms, price, etc.)
        });

        useEffect(() => {
            // Fetch the advertisement data from the backend API based on the provided ID
            axios.get(`http://localhost:8080/api/apartments/${id}`) // Change the API endpoint accordingly for other categories
                .then((response) => {
                    setFormData(response.data);
                })
                .catch((error) => {
                    console.error('Error fetching advertisement:', error);
                });
        }, [id]);

        const handleChange = (e) => {
            setFormData({
                ...formData,
                [e.target.name]: e.target.value,
            });
        };

        const handleSubmit = (e) => {
            e.preventDefault();
            // Send the updated advertisement data to the backend API for saving changes
            axios.put(`http://localhost:8080/api/apartments/${id}`, formData) // Change the API endpoint accordingly for other categories
                .then((response) => {
                    console.log('Advertisement updated:', response.data);
                    // Perform any additional actions after successful update
                })
                .catch((error) => {
                    console.error('Error updating advertisement:', error);
                });
        };
    };

    return (
        <div>
            <div className="title"><h2>Apartments List</h2><button>Add ad</button></div>
            {advertisements.length === 0 ? (
                <p>No advertisements found.</p>
            ) : (
                <table>
                    <tr>
                        <th>Type</th>
                        <th>Rent\Sell</th>
                        <th>Size</th>
                        <th>Rooms</th>
                        <th>Price</th>
                    </tr>
                    {advertisements.map((ad) => (
                        <tr key={ad.id}>
                            <td>{ad.type}</td>
                            <td>{ad.rentOrSell}</td>
                            <td>{ad.size}</td>
                            <td>{ad.rooms}</td>
                            <td>{ad.price}</td>
                            <button>Edit</button>
                            <button>Delete</button>
                        </tr>
                    ))}
                </table>
            )}
            <div>
                <h2>Edit Advertisement</h2>
                <form onSubmit={handleSubmit}>
                    {/* Display existing advertisement data in the form */}
                    <label>
                        Type:
                        <input type="text" name="type" value={formData.type} onChange={handleChange} />
                    </label>
                    {/* Add other fields */}
                    <button type="submit">Update Advertisement</button>
                </form>
            </div>
        </div>
    );
}
 export default Apartments;
