import React, {useEffect, useState} from "react";
import './apartments.css';
import Modal from "react-modal";
import {useParams} from "react-router-dom";

const EditAdvertisement = (props) => {
    const [formData, setFormData] = useState({
        type: '',
        // Add other fields here (e.g., rentOrSell, size, rooms, price, etc.)
    });

    useEffect(() => {
        // Fetch the advertisement data from the backend API based on the provided ID
        axios.get(`/api/apartments/${id}`)
            .then((response) => {
                setFormData(response.data);
            })
            .catch((error) => {
                console.error('Error fetching advertisement:', error);
            });
    }, [id]);

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        });
    };

    const handleDelete = () => {
        // Send a DELETE request to the backend API to delete the advertisement
        axios.delete(`/api/apartments/${id}`)
            .then((response) => {
                console.log('Advertisement deleted:', response.data);
                props.onClose(); // Call the onClose function from props to close the popup
            })
            .catch((error) => {
                console.error('Error deleting advertisement:', error);
            });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Send the updated advertisement data to the backend API for saving changes
        axios.put(`/api/apartments/${id}`, formData)
            .then((response) => {
                console.log('Advertisement updated:', response.data);
                setIsModalOpen(false); // Close the modal after successful update
            })
            .catch((error) => {
                console.error('Error updating advertisement:', error);
            });
    };

    const handleOpenModal = () => {
        setIsModalOpen(true);
    };

    const handleCloseModal = () => {
        setIsModalOpen(false);
        props.onClose(); // Call the onClose function from props to close the popup
    };

    return (
        <div>
            <h2>Edit Advertisement</h2>
            <button onClick={handleOpenModal}>Edit Advertisement</button>

            <Modal
                isOpen={isModalOpen}
                onRequestClose={handleCloseModal}
                contentLabel="Edit Advertisement Modal"
            >
                <h2>Edit Advertisement</h2>
                <form onSubmit={handleSubmit}>
                    {/* Display existing advertisement data in the form */}
                    <label>
                        Type:
                        <input type="text" name="type" value={formData.type} onChange={handleChange} />
                    </label>
                    {/* Add other fields */}
                    <button type="submit">Update Advertisement</button>
                    <button type="button" onClick={handleDelete}>Delete Advertisement</button>
                    <button type="button" onClick={handleCloseModal}>Cancel</button>
                </form>
            </Modal>
        </div>
    );
}
export default EditAdvertisement;
