import {useEffect, useState} from "react";
import axios from "axios";

function App() {
    const [advertisements, setAdvertisements] = useState([]);

    useEffect(() => {
        // Fetch the list of advertisements from the backend API
        axios.get('/api/apartments') // Change the API endpoint accordingly for other categories
            .then((response) => {
                setAdvertisements(response.data);
            })
            .catch((error) => {
                console.error('Error fetching advertisements:', error);
            });
    }, []);

    return (
        <div>
            <h2>Advertisement List</h2>
            {advertisements.length === 0 ? (
                <p>No advertisements found.</p>
            ) : (
                <ul>
                    {advertisements.map((ad) => (
                        <li key={ad.id}>
                            {/* Display the advertisement details here */}
                            <p>Type: {ad.type}</p>
                            {/* Display other advertisement fields */}
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}

export default App;
