import './App.css';
import React, {useState} from "react";
import axios from 'axios';

function App() {
  const [formData, setFormData] = useState({
    type: '',
    // Add other fields here (e.g., rentOrSell, size, rooms, price, etc.)
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const postUrl = 'http://localhost:8080/apartments';

  const handleSubmit = (e) => {
    e.preventDefault();
    const res = axios.get(postUrl) // Change the API endpoint accordingly for other categories
        .then((response) => {
          console.log('Advertisement added:', response.data);
          // Perform any additional actions after successful submission
        })
        .catch((error) => {
          console.error('Error adding advertisement:', error);
        });
  };

  return (
      <div>
          {}
        <h2>Add Advertisement</h2>
        <form onSubmit={handleSubmit}>
          {/* Add form fields here based on the required fields for each category */}
          <label>
            Type:
            <input type="text" name="type" value={formData.type} onChange={handleChange} />
          </label>
          {/* Add other fields */}
          <button type="submit">Add Advertisement</button>
        </form>
      </div>
  );
};

export default App;
