import React, {useEffect, useState} from "react";
import axios from "axios";
import './cars.css';
import Modal from "react-modal";

function Cars() {
    const API_URL = 'http://localhost:8080/api/cars';
    const headers = {
        "Accept": "application/json",
        "Access-Control-Allow-Origin": "*",
        "Content-Type": "application/json",
        "Access-Control-Allow-Methods": "GET,POST,OPTIONS,DELETE,PUT"
    };

    const [advertisements, setAdvertisements] = useState([]);

    const [newCar, setNewCar] = useState({
        manufacturer: '',
        model: '',
        year: '',
        hand: '',
        paint: '',
        distance: '',
        price: '',
    });
    const colorOptions = [
        { value: "White", label: "White" },
        { value: "Black", label: "Black" },
        { value: "Red", label: "Red" },
        { value: "Gray", label: "Gray" },
        { value: "Pink", label: "Pink" },
        { value: "Brown", label: "Brown" },
        { value: "Blue", label: "Blue" },
        { value: "Orange", label: "Orange" },
        { value: "Yellow", label: "Yellow" },
        { value: "Purple", label: "Purple" },
    ];

    const [showPopup, setShowPopup] = useState(false);
    const [editCarId, setEditCarId] = useState(null);
    const [minPriceFilter, setMinPriceFilter] = useState('');
    const [maxPriceFilter, setMaxPriceFilter] = useState('');
    const [isFilterOn, setIsFilterOn] = useState(false);
    const [sizeFilter, setSizeFilter] = useState('');

    const handleMinPriceFilter = (e) => {
        setMinPriceFilter(e.target.value);
    };
    const handleMaxPriceFilter = (e) => {
        setMaxPriceFilter(e.target.value);
    };

    useEffect(() => {
        fetchCars().then();
    }, []);

    const fetchCars = async () => {
        try {
            const response = await axios.get(API_URL, {headers});
            setAdvertisements(response.data);
        } catch (error) {
            console.error('Error fetching cars:', error);
        }
    };

    const handleAdd = () => {
        setShowPopup(true);
        setNewCar({
            manufacturer: '',
            model: '',
            year: '',
            hand: '',
            paint: '',
            distance: '',
            price: '',
        });
        setEditCarId(null);
    };

    const handleEdit = (car) => {
        setShowPopup(true);
        setNewCar({ ...car });
        setEditCarId(car.id);
    };

    const handleDelete = async (carId) => {
        try {
            await axios.delete(`${API_URL}/${carId}`,{
                headers});
            await fetchCars();
        } catch (error) {
            console.error('Error deleting car:', error);
        }
    };

    const handleFormChange = (e) => {
        const { name, value } = e.target;
        setNewCar((prevCar) => ({
            ...prevCar,
            [name]: value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log(newCar)
        try {
            if (editCarId) {
                await axios.put(`${API_URL}/${editCarId}`, newCar, {headers});
            } else {
                await axios.post(API_URL, newCar, {headers});
            }
            setShowPopup(false);
            await fetchCars();
        } catch (error) {
            console.error('Error submitting car:', error);
        }
    };

    const handleCancel = () => {
        setShowPopup(false);
        setNewCar({
            manufacturer: '',
            model: '',
            year: '',
            hand: '',
            paint: '',
            distance: '',
            price: '',
        });
        setEditCarId(null);
    };

    const filteredAdvertisements = advertisements
        .sort((a, b) => a.id - b.id)
        .filter((car) => {
            if (!isFilterOn) {
                return true;
            }

            if (!minPriceFilter && !maxPriceFilter) return true;

            const price = parseInt(car.price);

            if (minPriceFilter && !maxPriceFilter) {
                return price >= parseInt(minPriceFilter);
            }
            if (!minPriceFilter && maxPriceFilter) {
                return price <= parseInt(maxPriceFilter);
            }

            return price >= parseInt(minPriceFilter) && price <= parseInt(maxPriceFilter);
        });
    const handleToggleFilter = () => {
        setIsFilterOn(!isFilterOn);
    };

    const handleSizeFilter = (e) => {
        setSizeFilter(e.target.value);
    };

    return (
        <div>
            <h1>Cars Ads</h1>

            <div className={"header-div"}>
                <button className="new-ad-btn" onClick={handleAdd}>New Ad</button>
                <div className={"filter-price-div"}>
                    <div className={"filter-select-input"}>
                        <label>
                            <select value={sizeFilter} onChange={handleSizeFilter}>
                                <option value="New">New</option>
                                <option value="Good">Good</option>
                                <option value="Slightly bad">Slightly bad</option>
                                <option value="Bad">Bad</option>
                            </select>
                        </label>
                    </div>

                    <div className={"filter-price-input"}>
                        <label>
                            <input type="number" className="input-filter" value={minPriceFilter} min={0} max={maxPriceFilter} placeholder="From-" onChange={handleMinPriceFilter} />
                        </label>
                        <label>
                            <input type="number" className="input-filter" value={maxPriceFilter} min={minPriceFilter || 0} max={99999999} placeholder="To-" onChange={handleMaxPriceFilter} />
                        </label>
                    </div>
                    <button className={isFilterOn ? 'filter-button active' : 'filter-button'} onClick={handleToggleFilter}>
                        {isFilterOn ? 'Filter On' : 'Filter Off'}
                    </button>
                </div>
            </div>

            <table>
                <thead>
                <tr>
                    <th>Type</th>
                    <th>Condition</th>
                    <th>Price</th>
                    <th>Actions</th>
                </tr>
                </thead>
                <tbody>
                {filteredAdvertisements.map((car) => (
                    <tr key={car.id}>
                        <td>{car.manufacturer}</td>
                        <td>{car.model}</td>
                        <td>{car.year}</td>
                        <td>{car.hand}</td>
                        <td>{car.paint}</td>
                        <td>{car.distance}</td>
                        <td>{car.price}</td>
                        <td>
                            <button onClick={() => handleEdit(car)}>Edit</button>
                            <button onClick={() => handleDelete(car.id)}>Delete</button>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>

            <Modal isOpen={showPopup} ariaHideApp={false} title={editCarId ? 'Edit Ad' : 'Add New Ad'} onClose={() => setShowPopup(false)}>
                <form onSubmit={handleSubmit}>
                    <div>
                        <h2>{editCarId ? 'Edit Ad' : 'Add New Ad'}</h2>
                        <label>Manufacturer: </label>
                        <input
                            type="text"
                            name="manufacturer"
                            value={newCar.manufacturer}
                            required
                            onChange={handleFormChange}
                        />
                        <label>Model: </label>
                        <input
                            type="text"
                            name="model"
                            value={newCar.model}
                            required
                            onChange={handleFormChange}
                        />
                        <label>Year: </label>
                        <input
                            type="number"
                            name="year"
                            value={newCar.year}
                            required
                            min={1970}
                            max={2023}
                            onChange={handleFormChange}
                        />
                        <label>Hand: </label>
                        <input
                            type="number"
                            name="hand"
                            value={newCar.hand}
                            required
                            min={0}
                            max={20}
                            onChange={handleFormChange}
                        />
                        <label>Paint: </label>
                        <select name="condition" onChange={handleFormChange} options={colorOptions}>
                        </select>
                        <label>Distance: </label>
                        <input
                            type="number"
                            name="distance"
                            value={newCar.number}
                            min={0}
                            max={1000000}
                            maxLength={6}
                            onChange={handleFormChange}
                        />
                        {/*<label>Condition:*/}
                        {/*    <select name="condition" onChange={handleFormChange} options={colorOptions}>*/}
                        {/*    </select>*/}
                        {/*</label>*/}
                        <label>Price: </label>
                        <input
                            type="number"
                            name="price"
                            value={newCar.price}
                            onChange={handleFormChange}
                        />
                        <button type="submit">Save Ad</button>
                        <button type="button" onClick={handleCancel}>Cancel</button>
                    </div>
                </form>
            </Modal>
        </div>
    );
};
export default Cars;
