import React, {useEffect, useState} from "react";
import axios from "axios";
import './apartments.css';
import EditAdvertisement from "./editAdvertisement";
import {useParams} from "react-router-dom";
import Modal from "react-modal";
import async from "async";

function Apartments() {
    let [advertisements, setAdvertisements] = useState([]);
    const { id } = useParams();
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [showEditAdvertisement, setShowEditAdvertisement] = useState(false);
    const [minPrice, setMinPrice] = useState(0);
    const [maxPrice, setMaxPrice] = useState(null);
    const [isFilterOn, setIsFilterOn] = useState(false); // Initialize the filter as turned off

    const [formData, setFormData] = useState({
        type: '',
        rentOrSell: 'Rent',
        size: '',
        rooms: '',
        price: '',
    });
    const handleOpenModal = () => {
        setIsModalOpen(true);
    };

    const handleCloseModal = () => {
        setIsModalOpen(false);
    };

    useEffect(() => {
        // Fetch the list of advertisements from the backend API
        axios.get('http://localhost:8080/api/apartments', {
            headers: {
                "Accept": "application/json",
                "access-Control-Allow-Origin": "*",
                "content-Type": "application/json"
            }
        })
            .then((response) => {
                // console.log(response.data)
                setAdvertisements([...response.data]);
            })
            .catch((error) => {
                console.error('Error fetching advertisements:', error);
            });
    }, [id, formData]);

    const handleChange = (e) => {
        // console.log(formData)
        const value = e.target.value;
        setFormData({
            ...formData,
            [e.target.name]: value,
        });
    };

    const handleToggleEditAdvertisement = () => {
        setShowEditAdvertisement(!showEditAdvertisement);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        const data = {
            type: formData.type,
            rentOrSell: formData.rentOrSell,
            size: formData.size,
            rooms: formData.rooms,
            price: formData.price,

        };
        axios.post('http://localhost:8080/api/apartments', data, {
            headers: {
                "Accept": "application/json",
                "Access-Control-Allow-Origin": "*",
                "Content-Type": "application/json",
                "Access-Control-Allow-Methods": "GET,POST,OPTIONS,DELETE,PUT"
            }}) // Change the API endpoint accordingly for other categories
            .then((response) => {
                console.log('Advertisement added:', response.data);
                // setAdvertisements(response.data);
                setFormData(response.data);
                setIsModalOpen(false);
            })
            .catch((error) => {
                console.error('Error adding advertisement:', error);
            });
    }

    async function handleDelete(id) {
        console.log(id)
        await axios.delete(`http://localhost:8080/api/apartments/${id}`)
            .then((response) => {
                console.log('Advertisement deleted:', advertisements.splice(advertisements.indexOf(id), 1));
                // console.log('response', response.data);

                // setAdvertisements(advertisements.splice(advertisements.indexOf(id), 1))
                console.log('Advertisement', advertisements);
                setFormData(response.data);
            })
            .catch((error) => {
                console.error('Error deleting advertisement:', error);
            });
    };

    // Filter the advertisements based on the price range values
    const handleFilterByPrice = (e) => {
        if (advertisements.length > 1) {
            advertisements = advertisements.sort((a,b) => a.id - b.id);
        }
        if (isFilterOn) {
            return advertisements.filter(
                (ad) => ad.price >= minPrice && ad.price <= maxPrice
            );
        } else {
            return advertisements;
        }
    };

    return (
        <div>
            <h2>Add Advertisement</h2>

            <Modal
                isOpen={isModalOpen}
                ariaHideApp={false}
                onRequestClose={handleCloseModal}
                contentLabel="Add Advertisement Modal"
            >
                <h2>Add Advertisement</h2>
                <form onSubmit={handleSubmit}>
                    <label>Enter apartment type:
                        <input
                            type="text"
                            name="type"
                            placeholder="Apartment type"
                            required
                            value={formData.type}
                            onChange={handleChange}
                        />
                    </label>
                    <label>Choose for rent or for sell:
                        <label htmlFor="Rent">Rent</label>
                        <input
                            type="radio"
                            name="rentOrSell"
                            value="Rent"
                            checked={formData.rentOrSell === 'Rent'}
                            onChange={handleChange}
                        />
                        <label htmlFor="Sell">Sell</label>
                        <input
                            type="radio"
                            name="rentOrSell"
                            value="Sell"
                            checked={formData.rentOrSell === 'Sell'}
                            onChange={handleChange}
                        />
                    </label>
                    <label>Enter your rooms:
                        <input
                            type="number"
                            name="rooms"
                            min="0"
                            value={formData.rooms}
                            onChange={handleChange}
                        />
                    </label>
                    <label>Enter your size:
                        <input
                            type="number"
                            name="size"
                            min="0"
                            value={formData.size}
                            onChange={handleChange}
                        />
                    </label>
                    <label>Enter your price:
                        <input
                            type="number"
                            name="price"
                            min="0"
                            value={formData.price}
                            onChange={handleChange}
                        />
                    </label>
                    <button type="submit">Add Advertisement</button>
                    <button type="button" onClick={handleCloseModal}>Cancel</button>
                </form>
            </Modal>
            {/* Filter by price range*/}
            <div>
                <label>Filter by Price: </label>
                <input
                    type="number"
                    name="minPrice"
                    min="0"
                    max={maxPrice}
                    step="10"
                    placeholder="From"
                    value={minPrice}
                    onChange={(e) => setMinPrice(parseInt(e.target.value))}
                />

                <input
                    type="number"
                    name="maxPrice"
                    min={minPrice}
                    max="100000"
                    step="10"
                    placeholder="To"
                    value={maxPrice}
                    onChange={(e) => setMaxPrice(parseInt(e.target.value))}
                />
            </div>
            <div>
                <label>Filter On:</label>
                <input
                    type="checkbox"
                    checked={isFilterOn}
                    onChange={() => setIsFilterOn(!isFilterOn)}
                />
            </div>
            <div className="title"><h2>Apartments List</h2><button onClick={handleOpenModal}>Add ad</button></div>
            {advertisements.length === 0 ? (
                <p>No advertisements found.</p>
            ) : (
                <table>
                    <tbody>
                        <tr>
                            {/*<th>Id</th>*/}
                            <th>Type</th>
                            <th>Rent\Sell</th>
                            <th>Size</th>
                            <th>Rooms</th>
                            <th>Price</th>
                        </tr>
                        {handleFilterByPrice()
                            .map((ad) => (
                            <tr key={ad.id}>
                                {/*<td>{ad.id}</td>*/}
                                <td>{ad.type}</td>
                                <td>{ad.rentOrSell}</td>
                                <td>{ad.size}</td>
                                <td>{ad.rooms}</td>
                                <td>{ad.price}</td>
                                <td className={"tableButtons"}>
                                    <EditAdvertisement
                                        id={ad.id}
                                        type={ad.type}
                                        rentOrSell={ad.rentOrSell}
                                        size={ad.size === null ? '' : ad.size}
                                        rooms={ad.rooms}
                                        price={ad.price}
                                        onClose={handleToggleEditAdvertisement}
                                    />
                                    <button onClick={() =>  handleDelete(ad.id) }>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
}
 export default Apartments;
