import React, {useEffect, useState} from "react";
import axios from "axios";
import './apartments.css';
import EditAdvertisement from "./editAdvertisement";
import {useParams} from "react-router-dom";
import Modal from "react-modal";

function Apartments() {
    const [advertisements, setAdvertisements] = useState([]);
    const { id } = useParams();
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [showEditAdvertisement, setShowEditAdvertisement] = useState(false);
    const [maxPrice, setMaxPrice] = useState(1000);
    const [minPrice, setMinPrice] = useState(0);

    const [formData, setFormData] = useState({
        type: '',
        rentOrSell: '',
        size: 10,
        rooms: 1,
        price: 1,
    });
    const handleOpenModal = () => {
        setIsModalOpen(true);
    };

    const handleCloseModal = () => {
        setIsModalOpen(false);
    };

    useEffect(() => {
        // Fetch the list of advertisements from the backend API
        axios.get('http://localhost:8080/api/apartments', {
            headers: {
                "Accept": "application/json",
                "access-Control-Allow-Origin": "*",
                "content-Type": "application/json"
            }
        })
            .then((response) => {
                console.log(response.data)
                setAdvertisements([...response.data]);
            })
            .catch((error) => {
                console.error('Error fetching advertisements:', error);
            });
    }, [id, formData]);

    const handleChange = (e) => {
        console.log(formData)
        const value = e.target.value;
        setFormData({
            ...formData,
            [e.target.name]: value,
        });
    };

    const handleToggleEditAdvertisement = () => {
        setShowEditAdvertisement(!showEditAdvertisement);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        const data = {
            type: formData.type,
            rentOrSell: formData.rentOrSell,
            size: formData.size,
            rooms: formData.rooms,
            price: formData.price,

        };
        axios.post('http://localhost:8080/api/apartments', data, {
            headers: {
                "Accept": "application/json",
                "Access-Control-Allow-Origin": "*",
                "Content-Type": "application/json",
                "Access-Control-Allow-Methods": "GET,POST,OPTIONS,DELETE,PUT"
            }}) // Change the API endpoint accordingly for other categories
            .then((response) => {
                console.log('Advertisement added:', response.data);
                // setAdvertisements(response.data);
                setFormData(response.data);
                setIsModalOpen(false);
            })
            .catch((error) => {
                console.error('Error adding advertisement:', error);
            });
    }

    const handleDelete = (id) =>{
        axios.delete(`http://localhost:8080/api/apartments/${id}`)
            .then((response) => {
                console.log('Advertisement deleted:', id);
                setFormData(response.data);
            })
            .catch((error) => {
                console.error('Error deleting advertisement:', error);
            });
    };

    const handleFilterByPrice = (e) => {
        // Filter the advertisements based on the new minPrice and maxPrice values
        const filteredAdvertisements = advertisements.filter(
            (ad) => ad.price >= minPrice && ad.price <= maxPrice
        );
        return filteredAdvertisements;
    };

    return (
        <div>
            <h2>Add Advertisement</h2>

            <Modal
                isOpen={isModalOpen}
                ariaHideApp={false}
                onRequestClose={handleCloseModal}
                contentLabel="Add Advertisement Modal"
            >
                <h2>Add Advertisement</h2>
                <form onSubmit={handleSubmit}>
                    <label>Enter apartment type:
                        <input
                            type="text"
                            name="type"
                            value={formData.type}
                            onChange={handleChange}
                        />
                    </label>
                    <label>Choose for rent or sell:
                        <label htmlFor="rent">rent</label>
                        <input
                            type="radio"
                            name="rentOrSell"
                            value="rent"
                            checked={formData.rentOrSell === 'rent'}
                            onChange={handleChange}
                        />
                        <label htmlFor="sell">sell</label>
                        <input
                            type="radio"
                            name="rentOrSell"
                            value="sell"
                            checked={formData.rentOrSell === 'sell'}
                            onChange={handleChange}
                        />
                    </label>
                    <label>Enter your rooms:
                        <input
                            type="number"
                            name="rooms"
                            value={formData.rooms}
                            onChange={handleChange}
                        />
                    </label>
                    <label>Enter your size:
                        <input
                            type="number"
                            name="size"
                            value={formData.size}
                            onChange={handleChange}
                        />
                    </label>
                    <label>Enter your price:
                        <input
                            type="number"
                            name="price"
                            value={formData.price}
                            onChange={handleChange}
                        />
                    </label>
                    <button type="submit">Add Advertisement</button>
                    <button type="button" onClick={handleCloseModal}>Cancel</button>
                </form>
            </Modal>
            {/* Filter by price */}
            <div>
                <label>Filter by Price: </label>
                <label>Min Price:</label>
                <input
                    type="number"
                    name="minPrice"
                    min="0"
                    step="10"
                    value={minPrice}
                    onChange={(e) => setMinPrice(parseInt(e.target.value))}
                />
            </div>
            <div>
                <label>Max Price:</label>
                <input
                    type="number"
                    name="maxPrice"
                    min="0"
                    max="100000"
                    step="10"
                    value={maxPrice}
                    onChange={(e) => setMaxPrice(parseInt(e.target.value))}
                />
            </div>
            <div className="title"><h2>Apartments List</h2><button onClick={handleOpenModal}>Add ad</button></div>
            {advertisements.length === 0 ? (
                <p>No advertisements found.</p>
            ) : (
                <table>
                    <tbody>
                        <tr>
                            <th>Id</th>
                            <th>Type</th>
                            <th>Rent\Sell</th>
                            <th>Size</th>
                            <th>Rooms</th>
                            <th>Price</th>
                        </tr>
                        {handleFilterByPrice()
                            .map((ad) => (
                            <tr key={ad.id}>
                                <td>{ad.id}</td>
                                <td>{ad.type}</td>
                                <td>{ad.rentOrSell}</td>
                                <td>{ad.size}</td>
                                <td>{ad.rooms}</td>
                                <td>{ad.price}</td>
                                <td className={"tableButtons"}>
                                    <EditAdvertisement
                                        id={ad.id}
                                        type={ad.type}
                                        rentOrSell={ad.rentOrSell}
                                        size={ad.size}
                                        rooms={ad.rooms}
                                        price={ad.price}
                                        onClose={handleToggleEditAdvertisement} // Pass the function as prop
                                    />
                                    <button onClick={ (e) => handleDelete(ad.id, e) }>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
}
 export default Apartments;
