import React, {useEffect, useState} from "react";
import axios from "axios";
import './cars.css';
import EditApartment from "./editApartment";
import {useParams} from "react-router-dom";
import Modal from "react-modal";

function Cars() {
    let [advertisements, setAdvertisements] = useState([]);
    const { id } = useParams();
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [showEditAdvertisement, setShowEditAdvertisement] = useState(false);
    const [minPrice, setMinPrice] = useState(null);
    const [maxPrice, setMaxPrice] = useState(null);
    const [isFilterOn, setIsFilterOn] = useState(false); // Initialize the filter as turned off

    const [formData, setFormData] = useState({
        manufacturer: '',
        model: '',
        year: '',
        hand: '',
        paint: '',
        distance: '',
        price: '',
    });

    const handleOpenModal = () => {
        setIsModalOpen(true);
    };

    const handleCloseModal = () => {
        setIsModalOpen(false);
    };

    // Fetch the list of advertisements from the backend API
    useEffect(() => {
        axios.get('http://localhost:8080/api/cars', {
            headers: {
                "Accept": "application/json",
                "access-Control-Allow-Origin": "*",
                "content-Type": "application/json"
            }
        })
            .then((response) => {
                // console.log(response.data)
                setAdvertisements([...response.data]);
            })
            .catch((error) => {
                console.error('Error fetching advertisements:', error);
            });
    }, [id, formData]);

    const handleChange = (e) => {
        const value = e.target.value;
        setFormData({
            ...formData,
            [e.target.name]: value,
        });
    };

    const handleToggleEditAdvertisement = () => {
        setShowEditAdvertisement(!showEditAdvertisement);
    };

    // Add advertisement to the backend API
    const handleSubmit = (event) => {
        event.preventDefault();
        const data = {
            manufacturer: formData.manufacturer,
            model: formData.model,
            year: formData.year,
            hand: formData.hand,
            paint: formData.paint,
            distance: formData.distance,
            price: formData.price,
        };

        axios.post('http://localhost:8080/api/cars', data, {
            headers: {
                "Accept": "application/json",
                "Access-Control-Allow-Origin": "*",
                "Content-Type": "application/json",
                "Access-Control-Allow-Methods": "GET,POST,OPTIONS,DELETE,PUT"
            }})
            .then((response) => {
                setFormData(response.data);
                setIsModalOpen(false);
            })
            .catch((error) => {
                console.error('Error adding advertisement:', error);
            });
    }

    // Delete advertisement from the backend API
    const handleDelete = (id) => {
        axios.delete(`http://localhost:8080/api/car/${id}`)
            .then((response) => {
                const updatedAdvertisements = advertisements.filter((ad) => ad.id !== id);
                setAdvertisements(updatedAdvertisements);
            })
            .catch((error) => {
                console.error('Error deleting advertisement:', error);
            });
    };

    // Filter the advertisements based on the price range values
    const handleFilterByPrice = (e) => {
        if (advertisements.length > 1) {
            advertisements = advertisements.sort((a,b) => a.id - b.id);
        }
        if (isFilterOn) {
            return advertisements.filter(
                (ad) => ad.price >= minPrice && ad.price <= maxPrice
            );
        } else {
            return advertisements;
        }
    };
    const handleToggleFilter = () => {
        setIsFilterOn(!isFilterOn);
    };

    return (
        <div>
            <h2>Add Car</h2>

            <Modal
                isOpen={isModalOpen}
                ariaHideApp={false}
                onRequestClose={handleCloseModal}
                contentLabel="Add Advertisement Modal"
            >
                <h2>Add Car</h2>
                <form onSubmit={handleSubmit}>
                    <label>Enter car type:
                        <input
                            type="text"
                            name="manufacturer"
                            placeholder="Manufacturer"
                            required
                            value={formData.manufacturer}
                            onChange={handleChange}
                        />
                    </label>
                    <label>Enter car type:
                        <input
                            type="text"
                            name="manufacturer"
                            placeholder="Manufacturer"
                            required
                            value={formData.manufacturer}
                            onChange={handleChange}
                        />
                    </label>
                    <label>Enter your rooms:
                        <input
                            type="number"
                            name="rooms"
                            min="0"
                            value={formData.rooms}
                            onChange={handleChange}
                        />
                    </label>
                    <label>Enter your size:
                        <input
                            type="number"
                            name="size"
                            min="0"
                            value={formData.size}
                            onChange={handleChange}
                        />
                    </label>
                    <label>Enter your price:
                        <input
                            type="number"
                            name="price"
                            min="0"
                            value={formData.price}
                            onChange={handleChange}
                        />
                    </label>
                    <button type="submit">Add Advertisement</button>
                    <button type="button" onClick={handleCloseModal}>Cancel</button>
                </form>
            </Modal>
            <div>
                <label>Filter by Price: </label>
                <input
                    type="number"
                    name="minPrice"
                    min="0"
                    max={maxPrice}
                    step="10"
                    placeholder="From"
                    value={minPrice}
                    onChange={(e) => setMinPrice(parseInt(e.target.value))}
                />

                <input
                    type="number"
                    name="maxPrice"
                    min={minPrice}
                    max="100000"
                    step="10"
                    placeholder="To"
                    value={maxPrice}
                    onChange={(e) => setMaxPrice(parseInt(e.target.value))}
                />
                <label>Filter On:</label>
                <button className={isFilterOn ? 'filter-button active' : 'filter-button'} onClick={handleToggleFilter}>
                    {isFilterOn ? 'On' : 'Off'}
                </button>
            </div>
            <div className="title"><h2>Cars List</h2><button onClick={handleOpenModal}>Add ad</button></div>
            {advertisements.length === 0 ? (
                <p>No advertisements found.</p>
            ) : (
                <table>
                    <tbody>
                    <tr>
                        <th>Type</th>
                        <th>Rent\Sell</th>
                        <th>Size</th>
                        <th>Rooms</th>
                        <th>Price</th>
                    </tr>
                    {handleFilterByPrice()
                        .map((ad) => (
                            <tr key={ad.id}>
                                <td>{ad.type}</td>
                                <td>{ad.rentOrSell}</td>
                                <td>{ad.size}</td>
                                <td>{ad.rooms}</td>
                                <td>{ad.price}</td>
                                <td className={"tableButtons"}>
                                    <EditApartment
                                        id={ad.id}
                                        type={ad.type}
                                        rentOrSell={ad.rentOrSell}
                                        size={ad.size === null ? '' : ad.size}
                                        rooms={ad.rooms}
                                        price={ad.price}
                                        onClose={handleToggleEditAdvertisement}
                                    />
                                    <button onClick={() =>  handleDelete(ad.id) }>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
}
export default Cars;
