import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useHistory } from 'react-router-dom';
import Modal from 'react-modal';

const EditAdvertisement = () => {
    const { id } = useParams();
    const history = useHistory();
    const [isModalOpen, setIsModalOpen] = useState(false);

    // ... The rest of the code remains the same ...

    const handleOpenModal = () => {
        setIsModalOpen(true);
    };

    const handleCloseModal = () => {
        setIsModalOpen(false);
    };

    // ... The rest of the code remains the same ...

    return (
        <div>
            <h2>Edit Advertisement</h2>
            <button onClick={handleOpenModal}>Edit Advertisement</button>

            <Modal
                isOpen={isModalOpen}
                onRequestClose={handleCloseModal}
                contentLabel="Edit Advertisement Modal"
            >
                <h2>Edit Advertisement</h2>
                <form onSubmit={handleSubmit}>
                    {/* Display existing advertisement data in the form */}
                    <label>
                        Type:
                        <input type="text" name="type" value={formData.type} onChange={handleChange} />
                    </label>
                    {/* Add other fields */}
                    <button type="submit">Update Advertisement</button>
                    <button type="button" onClick={handleDelete}>Delete Advertisement</button>
                    <button type="button" onClick={handleCloseModal}>Cancel</button>
                </form>
            </Modal>

            {/* ... Add the form for adding a new advertisement with a similar modal */}
        </div>
    );
};

export default EditAdvertisement;
